    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import cors from "cors";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();

// CORS configuration to allow Chrome extension access
app.use(cors({
  origin: [
    /^chrome-extension:\/\/.*/,
    "https://6890239f-4e7c-48b1-ae06-0f1b134d2f42-00-2z7sf66begnj7.janeway.replit.dev",
    "http://localhost:5000"
  ],
  credentials: true
}));

app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen(port, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertAuctionSchema } from "@shared/schema";
import { auctionMonitor } from "./monitor";
import { facebookScraper } from "./facebook-scraper";

export async function registerRoutes(app: Express): Promise<Server> {
  // Get all auctions with optional filters
  app.get("/api/auctions", async (req, res) => {
    try {
      const { search, opalType, status, priceRange, limit, offset } = req.query;
      const result = await storage.getAuctions({
        search: search as string,
        opalType: opalType as string,
        status: status as string,
        priceRange: priceRange as string,
        limit: limit ? parseInt(limit as string) : undefined,
        offset: offset ? parseInt(offset as string) : undefined,
      });
      res.json(result);
    } catch (error) {
      console.error("Error fetching auctions:", error);
      res.status(500).json({ message: "Failed to fetch auctions" });
    }
  });

  // Get single auction by ID
  app.get("/api/auctions/:id", async (req, res) => {
    try {
      const auction = await storage.getAuction(req.params.id);
      if (!auction) {
        return res.status(404).json({ message: "Auction not found" });
      }
      res.json(auction);
    } catch (error) {
      console.error("Error fetching auction:", error);
      res.status(500).json({ message: "Failed to fetch auction" });
    }
  });

  // Create new auction
  app.post("/api/auctions", async (req, res) => {
    try {
      console.log("Received auction data:", req.body);
      const validatedData = insertAuctionSchema.parse(req.body);
      console.log("Validated data:", validatedData);
      const auction = await storage.createAuction(validatedData);
      res.status(201).json(auction);
    } catch (error) {
      console.error("Error creating auction:", error);
      if (error instanceof Error && error.name === "ZodError") {
        console.error("Zod validation error:", JSON.stringify(error, null, 2));
        return res.status(400).json({ message: "Invalid auction data", errors: error });
      }
      res.status(500).json({ message: "Failed to create auction" });
    }
  });

  // Bulk create auctions for live dealers
  app.post("/api/auctions/bulk", async (req, res) => {
    try {
      const { auctions } = req.body;
      if (!Array.isArray(auctions)) {
        return res.status(400).json({ message: "Auctions must be an array" });
      }

      const createdAuctions = [];
      const errors = [];

      for (let i = 0; i < auctions.length; i++) {
        try {
          const validatedData = insertAuctionSchema.parse(auctions[i]);
          const auction = await storage.createAuction(validatedData);
          createdAuctions.push(auction);
        } catch (error) {
          console.error(`Error creating auction ${i}:`, error);
          errors.push({ index: i, error: error instanceof Error ? error.message : "Unknown error" });
        }
      }

      res.status(201).json({ 
        created: createdAuctions.length,
        total: auctions.length,
        auctions: createdAuctions,
        errors: errors
      });
    } catch (error) {
      console.error("Error in bulk create:", error);
      res.status(500).json({ message: "Failed to create auctions" });
    }
  });

  // Update auction - enhanced for Chrome extension bid updates
  app.patch("/api/auctions/:id", async (req, res) => {
    try {
      console.log(`üîÑ PATCH /api/auctions/${req.params.id} - Chrome extension update:`, req.body);

      const partialData = insertAuctionSchema.partial().parse(req.body);
      console.log("‚úÖ Validated partial data:", partialData);

      const auction = await storage.updateAuction(req.params.id, partialData);
      if (!auction) {
        console.error(`‚ùå Auction ${req.params.id} not found`);
        return res.status(404).json({ message: "Auction not found" });
      }

      console.log(`‚úÖ Updated auction ${req.params.id}:`, auction);
      res.json(auction);
    } catch (error) {
      console.error("‚ùå Error updating auction:", error);
      if (error instanceof Error && error.name === "ZodError") {
        console.error("Zod validation error:", JSON.stringify(error, null, 2));
        return res.status(400).json({ message: "Invalid auction data", errors: error });
      }
      res.status(500).json({ message: "Failed to update auction" });
    }
  });

  // Delete auction
  app.delete("/api/auctions/:id", async (req, res) => {
    try {
      const success = await storage.deleteAuction(req.params.id);
      if (!success) {
        return res.status(404).json({ message: "Auction not found" });
      }
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting auction:", error);
      res.status(500).json({ message: "Failed to delete auction" });
    }
  });

  // Get analytics
  app.get("/api/analytics", async (req, res) => {
    try {
      const analytics = await storage.getAnalytics();
      res.json(analytics);
    } catch (error) {
      console.error("Error fetching analytics:", error);
      res.status(500).json({ message: "Failed to fetch analytics" });
    }
  });

  // Export auctions
  app.post("/api/export", async (req, res) => {
    try {
      const { exportType, format, dateRange, includeFields } = req.body;

      let auctions;
      if (exportType === "won-auctions") {
        const result = await storage.getAuctions({ status: "won" });
        auctions = result.auctions;
      } else if (exportType === "date-range" && dateRange) {
        // Filter by date range if provided
        const result = await storage.getAuctions();
        auctions = result.auctions.filter(auction => {
          const createdAt = new Date(auction.createdAt);
          const startDate = new Date(dateRange.start);
          const endDate = new Date(dateRange.end);
          return createdAt >= startDate && createdAt <= endDate;
        });
      } else {
        const result = await storage.getAuctions();
        auctions = result.auctions;
      }

      // Filter fields based on includeFields selection
      const filteredAuctions = auctions.map(auction => {
        const filtered: any = {};

        if (includeFields?.opalDetails) {
          filtered.opalType = auction.opalType;
          filtered.weight = auction.weight;
          filtered.description = auction.description;
          filtered.shape = auction.shape;
        }

        if (includeFields?.prices) {
          filtered.startingBid = auction.startingBid;
          filtered.currentBid = auction.currentBid;
          filtered.maxBid = auction.maxBid;
        }

        if (includeFields?.groupInfo) {
          filtered.facebookGroup = auction.facebookGroup;
        }

        if (includeFields?.status) {
          filtered.status = auction.status;
        }

        if (includeFields?.dates) {
          filtered.endTime = auction.endTime;
          filtered.createdAt = auction.createdAt;
          filtered.updatedAt = auction.updatedAt;
        }

        if (includeFields?.notes) {
          filtered.notes = auction.notes;
        }

        if (includeFields?.urls) {
          filtered.postUrl = auction.postUrl;
        }

        if (includeFields?.origin) {
          filtered.origin = auction.origin;
        }

        return filtered;
      });

      res.json({
        data: filteredAuctions,
        format,
        exportType,
        timestamp: new Date().toISOString(),
        count: filteredAuctions.length
      });
    } catch (error) {
      console.error("Error exporting data:", error);
      res.status(500).json({ message: "Failed to export data" });
    }
  });

  // Auction monitoring endpoints
  app.get("/api/monitor/status", async (req, res) => {
    res.json({ running: false, message: "Chrome extension handles detection" });
  });

  app.post("/api/monitor/start", async (req, res) => {
    res.json({ message: "Server monitoring disabled - use Chrome extension", status: { running: false } });
  });

  app.post("/api/monitor/stop", async (req, res) => {
    res.json({ message: "Server monitoring already disabled", status: { running: false } });
  });

  // AUTO-MONITOR: Check Facebook URLs from auctions  
  app.post("/api/monitor/check", async (req, res) => {
    try {
      // Get all active auctions with Facebook URLs
      const result = await storage.getAuctions({ status: "active" });
      const activeAuctions = result.auctions.filter(auction => 
        auction.postUrl && auction.postUrl.includes('facebook.com')
      );

      if (activeAuctions.length === 0) {
        return res.json({ 
          message: "No active Facebook auctions to monitor",
          monitored: 0,
          updates: [],
          auctions: []
        });
      }

      console.log(`üîç Auto-checking ${activeAuctions.length} active Facebook auction URLs`);

      const updates: any[] = [];

      // Check each auction URL for bid updates
      for (const auction of activeAuctions) {
        if (auction.postUrl && auction.postUrl.includes('facebook.com')) {
          console.log(`üìä Checking auction ${auction.opalType} (${auction.id}): ${auction.postUrl}`);

          try {
            const currentBidNum = parseInt(auction.currentBid || auction.startingBid);

            // REAL FACEBOOK SCRAPING - Get actual bids from the Facebook post
            console.log(`üîç Scraping Facebook post for real bids: ${auction.postUrl}`);
            const scrapedData = await facebookScraper.scrapeFacebookPost(auction.postUrl);

            if (scrapedData.currentBid && scrapedData.currentBid > currentBidNum) {
              console.log(`üí∞ REAL BID DETECTED: $${scrapedData.currentBid} (was $${currentBidNum}) - ${scrapedData.bidCount} total bids`);

              // Update the auction with REAL bid info from Facebook
              const updated = await storage.updateAuction(auction.id, {
                currentBid: scrapedData.currentBid.toString(),
                bidCount: scrapedData.bidCount,
                lastUpdated: new Date().toISOString()
              });

              if (updated) {
                updates.push({
                  id: auction.id,
                  opalType: auction.opalType,
                  previousBid: currentBidNum,
                  newBid: scrapedData.currentBid,
                  bidCount: scrapedData.bidCount,
                  url: auction.postUrl,
                  scrapedComments: scrapedData.comments.slice(0, 3) // Show sample comments
                });
                console.log(`‚úÖ Updated auction ${auction.id} with REAL Facebook bid: $${scrapedData.currentBid}`);
              }
            } else if (scrapedData.currentBid) {
              console.log(`üìä No new bids - current: $${scrapedData.currentBid}, tracked: $${currentBidNum}`);
            } else {
              console.log(`üîç No bids found in Facebook post comments`);
            }
          } catch (error) {
            console.error(`‚ùå Error checking auction ${auction.id}:`, error);
          }
        }
      }

      res.json({ 
        message: `Automatically checked ${activeAuctions.length} Facebook auction URLs`,
        monitored: activeAuctions.length,
        updates: updates,
        auctions: activeAuctions.map(a => ({ 
          id: a.id, 
          opalType: a.opalType,
          url: a.postUrl, 
          currentBid: a.currentBid || a.startingBid 
        }))
      });
    } catch (error) {
      console.error("Error in auto-monitoring:", error);
      res.status(500).json({ message: "Failed to check auction URLs" });
    }
  });

  // Webhook endpoint for external bid notifications
  app.post("/api/webhooks/bid-update", async (req, res) => {
    try {
      const { auctionUrl, bidAmount, bidderName, source } = req.body;

      console.log(`üìû Webhook received: $${bidAmount} from ${bidderName} via ${source}`);

      // Find auction by URL
      const { auctions } = await storage.getAuctions({ status: 'active' });
      const auction = auctions.find(a => a.postUrl === auctionUrl);

      if (auction) {
        const currentBid = parseFloat(auction.currentBid || auction.startingBid);
        const newBid = parseFloat(bidAmount);

        if (newBid > currentBid) {
          await storage.updateAuction(auction.id, {
            currentBid: bidAmount.toString(),
            currentBidder: bidderName || 'Webhook User'
          });

          console.log(`‚úÖ Webhook updated: $${currentBid} ‚Üí $${newBid}`);
          res.json({ success: true, message: `Updated to $${newBid}` });
        } else {
          res.json({ success: false, message: `Bid $${newBid} not higher than current $${currentBid}` });
        }
      } else {
        res.status(404).json({ success: false, message: 'Auction not found' });
      }
    } catch (error) {
      console.error('‚ùå Webhook error:', error);
      res.status(500).json({ success: false, message: 'Webhook failed' });
    }
  });

  const httpServer = createServer(app);

  // Server monitoring disabled - Chrome extension handles all detection
  console.log("üöÄ Server ready - Chrome extension will handle bid detection...");
  // auctionMonitor.start();

  return httpServer;
}

server/storage.ts.content:
import { type Auction, type InsertAuction } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  getAuction(id: string): Promise<Auction | undefined>;
  getAuctions(filters?: {
    search?: string;
    opalType?: string;
    status?: string;
    priceRange?: string;
    limit?: number;
    offset?: number;
  }): Promise<{ auctions: Auction[]; total: number }>;
  createAuction(auction: InsertAuction): Promise<Auction>;
  updateAuction(id: string, auction: Partial<InsertAuction>): Promise<Auction | undefined>;
  deleteAuction(id: string): Promise<boolean>;
  getAnalytics(): Promise<{
    totalAuctions: number;
    activeAuctions: number;
    wonAuctions: number;
    avgPrice: number;
    priceRange: { min: number; max: number };
    winRate: number;
    groupStats: Array<{ group: string; auctions: number; winRate: number; avgPrice: number }>;
    recentAuctions: Auction[];
  }>;
}

export class MemStorage implements IStorage {
  private auctions: Map<string, Auction>;

  constructor() {
    this.auctions = new Map();
  }

  async getAuction(id: string): Promise<Auction | undefined> {
    return this.auctions.get(id);
  }

  async getAuctions(filters?: {
    search?: string;
    opalType?: string;
    status?: string;
    priceRange?: string;
    limit?: number;
    offset?: number;
  }): Promise<{ auctions: Auction[]; total: number }> {
    let allAuctions = Array.from(this.auctions.values());

    // Apply filters
    if (filters?.search) {
      const searchTerm = filters.search.toLowerCase();
      allAuctions = allAuctions.filter(auction => 
        auction.opalType.toLowerCase().includes(searchTerm) ||
        auction.description?.toLowerCase().includes(searchTerm) ||
        auction.facebookGroup.toLowerCase().includes(searchTerm) ||
        auction.origin?.toLowerCase().includes(searchTerm)
      );
    }

    if (filters?.opalType && filters.opalType !== "all") {
      allAuctions = allAuctions.filter(auction => auction.opalType === filters.opalType);
    }

    if (filters?.status && filters.status !== "all") {
      allAuctions = allAuctions.filter(auction => auction.status === filters.status);
    }

    if (filters?.priceRange && filters.priceRange !== "all") {
      allAuctions = allAuctions.filter(auction => {
        const currentBid = parseFloat(auction.currentBid || auction.startingBid);
        switch (filters.priceRange) {
          case "$0 - $100": return currentBid <= 100;
          case "$100 - $500": return currentBid > 100 && currentBid <= 500;
          case "$500 - $1000": return currentBid > 500 && currentBid <= 1000;
          case "$1000+": return currentBid > 1000;
          default: return true;
        }
      });
    }

    // Sort by creation date (newest first)
    allAuctions.sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());

    const total = allAuctions.length;
    const limit = filters?.limit || 50;
    const offset = filters?.offset || 0;
    const paginatedAuctions = allAuctions.slice(offset, offset + limit);

    return { auctions: paginatedAuctions, total };
  }

  async createAuction(insertAuction: InsertAuction): Promise<Auction> {
    const id = randomUUID();
    const now = new Date();
    const auction: Auction = {
      ...insertAuction,
      id,
      createdAt: now,
      updatedAt: now,
      description: insertAuction.description || null,
      origin: insertAuction.origin || null,
      shape: insertAuction.shape || null,
      postUrl: insertAuction.postUrl || null,
      currentBid: insertAuction.currentBid || null,
      maxBid: insertAuction.maxBid || null,
      notes: insertAuction.notes || null,
      isWatchlist: insertAuction.isWatchlist || false,
    };
    this.auctions.set(id, auction);
    return auction;
  }

  async updateAuction(id: string, updateData: Partial<InsertAuction>): Promise<Auction | undefined> {
    const existingAuction = this.auctions.get(id);
    if (!existingAuction) return undefined;

    const updatedAuction: Auction = {
      ...existingAuction,
      ...updateData,
      updatedAt: new Date(),
    };
    this.auctions.set(id, updatedAuction);
    return updatedAuction;
  }

  async deleteAuction(id: string): Promise<boolean> {
    return this.auctions.delete(id);
  }

  async getAnalytics(): Promise<{
    totalAuctions: number;
    activeAuctions: number;
    wonAuctions: number;
    avgPrice: number;
    priceRange: { min: number; max: number };
    winRate: number;
    groupStats: Array<{ group: string; auctions: number; winRate: number; avgPrice: number }>;
    recentAuctions: Auction[];
  }> {
    const allAuctions = Array.from(this.auctions.values());
    const totalAuctions = allAuctions.length;
    const activeAuctions = allAuctions.filter(a => a.status === "active").length;
    const wonAuctions = allAuctions.filter(a => a.status === "won").length;
    const endedAuctions = allAuctions.filter(a => a.status === "ended" || a.status === "won" || a.status === "lost").length;

    // Calculate average price and price range
    const prices = allAuctions.map(a => parseFloat(a.currentBid || a.startingBid));
    const avgPrice = prices.length > 0 ? prices.reduce((sum, price) => sum + price, 0) / prices.length : 0;
    const priceRange = prices.length > 0 ? { min: Math.min(...prices), max: Math.max(...prices) } : { min: 0, max: 0 };

    // Calculate win rate
    const winRate = endedAuctions > 0 ? (wonAuctions / endedAuctions) * 100 : 0;

    // Calculate group statistics
    const groupMap = new Map<string, { auctions: number; won: number; totalPrice: number }>();
    allAuctions.forEach(auction => {
      const group = auction.facebookGroup;
      const existing = groupMap.get(group) || { auctions: 0, won: 0, totalPrice: 0 };
      existing.auctions++;
      if (auction.status === "won") existing.won++;
      existing.totalPrice += parseFloat(auction.currentBid || auction.startingBid);
      groupMap.set(group, existing);
    });

    const groupStats = Array.from(groupMap.entries()).map(([group, stats]) => ({
      group,
      auctions: stats.auctions,
      winRate: stats.auctions > 0 ? (stats.won / stats.auctions) * 100 : 0,
      avgPrice: stats.auctions > 0 ? stats.totalPrice / stats.auctions : 0,
    }));

    // Get recent auctions (last 5)
    const recentAuctions = allAuctions
      .sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime())
      .slice(0, 5);

    return {
      totalAuctions,
      activeAuctions,
      wonAuctions,
      avgPrice,
      priceRange,
      winRate,
      groupStats,
      recentAuctions,
    };
  }
}

export const storage = new MemStorage();

