import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Copy, Facebook, ExternalLink } from "lucide-react";

const addAuctionSchema = z.object({
  opalType: z.string().min(1, "Opal type is required"),
  weight: z.string().min(1, "Weight is required").regex(/^\d+\.?\d*$/, "Weight must be a valid number"),
  description: z.string().optional(),
  origin: z.string().optional(),
  shape: z.string().optional(),
  facebookGroup: z.string().min(1, "Facebook group is required"),
  postUrl: z.string().url().optional().or(z.literal("")),
  startingBid: z.string().min(1, "Starting bid is required").regex(/^\d+\.?\d*$/, "Starting bid must be a valid number"),
  currentBid: z.string().regex(/^\d*\.?\d*$/, "Current bid must be a valid number").optional(),
  maxBid: z.string().regex(/^\d*\.?\d*$/, "Max bid must be a valid number").optional(),
  endTime: z.string().min(1, "End time is required"),
  status: z.enum(["active", "ended", "won", "lost"]).default("active"),
  notes: z.string().optional(),
  isWatchlist: z.boolean().default(false),
});

type AddAuctionForm = z.infer<typeof addAuctionSchema>;

export default function AddAuction() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [showPostGenerator, setShowPostGenerator] = useState(false);
  const [generatedPost, setGeneratedPost] = useState("");
  const [createdAuctionId, setCreatedAuctionId] = useState<string | null>(null);

  const form = useForm<AddAuctionForm>({
    resolver: zodResolver(addAuctionSchema),
    defaultValues: {
      opalType: "",
      weight: "",
      description: "",
      origin: "",
      shape: "",
      facebookGroup: "",
      postUrl: "",
      startingBid: "",
      currentBid: "",
      maxBid: "",
      endTime: "",
      status: "active",
      notes: "",
      isWatchlist: false,
    },
  });

  const createAuctionMutation = useMutation({
    mutationFn: async (data: AddAuctionForm) => {
      const response = await apiRequest("POST", "/api/auctions", {
        ...data,
        weight: parseFloat(data.weight).toString(),
        startingBid: parseFloat(data.startingBid).toString(),
        currentBid: data.currentBid ? parseFloat(data.currentBid).toString() : undefined,
        maxBid: data.maxBid ? parseFloat(data.maxBid).toString() : undefined,
        endTime: data.endTime, // Send as string, let server handle conversion
      });
      return response.json();
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ["/api/auctions"] });
      queryClient.invalidateQueries({ queryKey: ["/api/analytics"] });
      setCreatedAuctionId(data.id);
      toast({
        title: "Success",
        description: "Auction created successfully!",
      });
      // Don't redirect immediately, show post generator option
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to create auction. Please try again.",
        variant: "destructive",
      });
      console.error("Create auction error:", error);
    },
  });

  const onSubmit = (data: AddAuctionForm) => {
    createAuctionMutation.mutate(data);
  };

  const handleCancel = () => {
    setLocation("/");
  };

  return (
    <div className="p-6">
      <div className="max-w-2xl mx-auto">
        <h2 className="text-2xl font-semibold text-foreground mb-6">Add New Auction</h2>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="bg-card rounded-lg border border-border p-6 space-y-6">
            {/* Opal Details Section */}
            <div>
              <h3 className="text-lg font-medium text-foreground mb-4">Opal Details</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <FormField
                  control={form.control}
                  name="opalType"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Opal Type *</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value}>
                        <FormControl>
                          <SelectTrigger data-testid="select-opal-type">
                            <SelectValue placeholder="Select opal type" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="Black Opal">Black Opal</SelectItem>
                          <SelectItem value="Crystal Opal">Crystal Opal</SelectItem>
                          <SelectItem value="Boulder Opal">Boulder Opal</SelectItem>
                          <SelectItem value="White Opal">White Opal</SelectItem>
                          <SelectItem value="Fire Opal">Fire Opal</SelectItem>
                          <SelectItem value="Matrix Opal">Matrix Opal</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="weight"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Weight (carats) *</FormLabel>
                      <FormControl>
                        <Input 
                          type="number" 
                          step="0.1" 
                          placeholder="e.g., 2.5" 
                          {...field}
                          data-testid="input-weight"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="description"
                  render={({ field }) => (
                    <FormItem className="md:col-span-2">
                      <FormLabel>Description</FormLabel>
                      <FormControl>
                        <Textarea 
                          rows={3}
                          placeholder="Describe the opal (color, pattern, origin, etc.)"
                          {...field}
                          data-testid="input-description"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="origin"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Origin/Location</FormLabel>
                      <FormControl>
                        <Input 
                          placeholder="e.g., Lightning Ridge, Coober Pedy"
                          {...field}
                          data-testid="input-origin"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="shape"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Shape/Cut</FormLabel>
                      <FormControl>
                        <Input 
                          placeholder="e.g., Oval, Freeform, Cabochon"
                          {...field}
                          data-testid="input-shape"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>
            </div>

            {/* Auction Details Section */}
            <div className="border-t border-border pt-6">
              <h3 className="text-lg font-medium text-foreground mb-4">Auction Details</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <FormField
                  control={form.control}
                  name="facebookGroup"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Facebook Group *</FormLabel>
                      <FormControl>
                        <Input 
                          placeholder="e.g., Lightning Ridge Opal Group"
                          {...field}
                          data-testid="input-facebook-group"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="postUrl"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Auction Post URL</FormLabel>
                      <FormControl>
                        <Input 
                          type="url"
                          placeholder="https://facebook.com/groups/..."
                          {...field}
                          data-testid="input-post-url"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="startingBid"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Starting Bid *</FormLabel>
                      <FormControl>
                        <div className="relative">
                          <span className="absolute left-3 top-2 text-muted-foreground">$</span>
                          <Input 
                            type="number"
                            step="0.01"
                            placeholder="0.00"
                            className="pl-8"
                            {...field}
                            data-testid="input-starting-bid"
                          />
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="currentBid"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Current Bid</FormLabel>
                      <FormControl>
                        <div className="relative">
                          <span className="absolute left-3 top-2 text-muted-foreground">$</span>
                          <Input 
                            type="number"
                            step="0.01"
                            placeholder="0.00"
                            className="pl-8"
                            {...field}
                            data-testid="input-current-bid"
                          />
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="endTime"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>End Date & Time *</FormLabel>
                      <FormControl>
                        <div className="space-y-2">
                          <Input 
                            type="datetime-local"
                            {...field}
                            data-testid="input-end-time"
                          />
                          <p className="text-xs text-muted-foreground">
                            Or enter as: DD/MM/YYYY HH:MM (e.g., 04/01/2025 15:30)
                          </p>
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="status"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Status</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value}>
                        <FormControl>
                          <SelectTrigger data-testid="select-status">
                            <SelectValue />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="active">Active</SelectItem>
                          <SelectItem value="ended">Ended</SelectItem>
                          <SelectItem value="won">Won</SelectItem>
                          <SelectItem value="lost">Lost</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>
            </div>


            {/* Form Actions */}
            <div className="flex space-x-4 pt-6">
              <Button 
                type="submit" 
                className="flex-1"
                disabled={createAuctionMutation.isPending}
                data-testid="button-save-auction"
              >
                {createAuctionMutation.isPending ? "Saving..." : "Save Auction"}
              </Button>
              <Button 
                type="button" 
                variant="outline"
                onClick={handleCancel}
                data-testid="button-cancel"
              >
                Cancel
              </Button>
            </div>
          </form>
        </Form>
      </div>
    </div>
  );
}

client/src/pages/analytics.tsx.content:
import { useQuery } from "@tanstack/react-query";
import PriceTrendChart from "@/components/charts/price-trend-chart";
import WinRateChart from "@/components/charts/win-rate-chart";

interface Analytics {
  totalAuctions: number;
  activeAuctions: number;
  wonAuctions: number;
  avgPrice: number;
  priceRange: { min: number; max: number };
  winRate: number;
  groupStats: Array<{
    group: string;
    auctions: number;
    winRate: number;
    avgPrice: number;
  }>;
  recentAuctions: any[];
}

export default function Analytics() {
  const { data: analytics, isLoading } = useQuery<Analytics>({
    queryKey: ["/api/analytics"],
  });

  const formatCurrency = (value: number) => `$${Math.round(value)}`;

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="animate-pulse space-y-6">
          <div className="h-8 bg-muted rounded w-64"></div>
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            <div className="bg-card rounded-lg border border-border p-6 h-80"></div>
            <div className="bg-card rounded-lg border border-border p-6 h-80"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6">
      <h2 className="text-2xl font-semibold text-foreground mb-6">Analytics & Insights</h2>

      {/* Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
        <PriceTrendChart />
        <WinRateChart analytics={analytics} />
      </div>

      {/* Detailed Analytics */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        <div className="lg:col-span-2 bg-card rounded-lg border border-border">
          <div className="p-6 border-b border-border">
            <h3 className="text-lg font-semibold text-foreground">Performance by Group</h3>
          </div>
          <div className="p-6">
            {analytics?.groupStats?.length === 0 ? (
              <div className="text-center py-8">
                <p className="text-muted-foreground">No group data available</p>
                <p className="text-sm text-muted-foreground mt-1">Add some auctions to see group performance</p>
              </div>
            ) : (
              <div className="space-y-4">
                {analytics?.groupStats?.map((group, index) => (
                  <div key={group.group} className="flex items-center justify-between p-4 bg-muted/50 rounded-lg" data-testid={`group-stats-${index}`}>
                    <div>
                      <p className="font-medium text-foreground">{group.group}</p>
                      <p className="text-sm text-muted-foreground">{group.auctions} auctions participated</p>
                    </div>
                    <div className="text-right">
                      <p className="font-semibold text-foreground">{Math.round(group.winRate)}% Win Rate</p>
                      <p className="text-sm text-muted-foreground">Avg: {formatCurrency(group.avgPrice)}</p>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        <div className="bg-card rounded-lg border border-border">
          <div className="p-6 border-b border-border">
            <h3 className="text-lg font-semibold text-foreground">Key Metrics</h3>
          </div>
          <div className="p-6 space-y-6">
            <div>
              <div className="flex items-center justify-between mb-2">
                <span className="text-sm text-muted-foreground">Total Spent</span>
                <span className="font-semibold text-foreground" data-testid="total-spent">
                  {formatCurrency((analytics?.wonAuctions || 0) * (analytics?.avgPrice || 0))}
                </span>
              </div>
              <div className="w-full bg-muted rounded-full h-2">
                <div 
                  className="bg-primary h-2 rounded-full" 
                  style={{ width: `${Math.min(100, ((analytics?.wonAuctions || 0) / Math.max(1, analytics?.totalAuctions || 1)) * 100)}%` }}
                ></div>
              </div>
            </div>

            <div>
              <div className="flex items-center justify-between mb-2">
                <span className="text-sm text-muted-foreground">Average Bid</span>
                <span className="font-semibold text-foreground" data-testid="average-bid">
                  {formatCurrency(analytics?.avgPrice || 0)}
                </span>
              </div>
              <div className="w-full bg-muted rounded-full h-2">
                <div className="bg-accent h-2 rounded-full" style={{ width: "65%" }}></div>
              </div>
            </div>

            <div>
              <div className="flex items-center justify-between mb-2">
                <span className="text-sm text-muted-foreground">Success Rate</span>
                <span className="font-semibold text-foreground" data-testid="success-rate">
                  {Math.round(analytics?.winRate || 0)}%
                </span>
              </div>
              <div className="w-full bg-muted rounded-full h-2">
                <div 
                  className="bg-amber-500 h-2 rounded-full" 
                  style={{ width: `${analytics?.winRate || 0}%` }}
                ></div>
              </div>
            </div>

            <div className="pt-4 border-t border-border">
              <div className="text-center">
                <p className="text-2xl font-bold text-foreground" data-testid="opals-won">
                  {analytics?.wonAuctions || 0}
                </p>
                <p className="text-sm text-muted-foreground">Opals Won</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/auctions.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Eye, Edit, Plus } from "lucide-react";
import AuctionTable from "@/components/auction-table";

interface Auction {
  id: string;
  opalType: string;
  weight: string;
  description?: string;
  facebookGroup: string;
  postUrl?: string;
  startingBid: string;
  currentBid?: string;
  endTime: string;
  status: "active" | "ended" | "won" | "lost";
  updatedAt: string;
}

interface AuctionsResponse {
  auctions: Auction[];
  total: number;
}

export default function Auctions() {
  const [search, setSearch] = useState("");
  const [opalType, setOpalType] = useState("all");
  const [status, setStatus] = useState("all");
  const [priceRange, setPriceRange] = useState("all");
  const [currentPage, setCurrentPage] = useState(1);
  const limit = 10;

  const { data, isLoading } = useQuery<AuctionsResponse>({
    queryKey: ["/api/auctions", search, opalType, status, priceRange, currentPage],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (search) params.append('search', search);
      if (opalType && opalType !== 'all') params.append('opalType', opalType);
      if (status && status !== 'all') params.append('status', status);
      if (priceRange && priceRange !== 'all') params.append('priceRange', priceRange);
      params.append('limit', limit.toString());
      params.append('offset', ((currentPage - 1) * limit).toString());

      const response = await fetch(`/api/auctions?${params.toString()}`);
      if (!response.ok) throw new Error('Failed to fetch auctions');
      return response.json();
    },
  });

  const totalPages = Math.ceil((data?.total || 0) / limit);

  const formatCurrency = (value: string) => `$${Math.round(parseFloat(value))}`;
  const formatDate = (dateString: string) => new Date(dateString).toLocaleDateString('en-US', {
    month: 'short',
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit'
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case "won": return "bg-accent/10 text-accent";
      case "active": return "bg-amber-500/10 text-amber-600";
      case "lost": return "bg-destructive/10 text-destructive";
      case "ended": return "bg-muted text-muted-foreground";
      default: return "bg-muted text-muted-foreground";
    }
  };

  return (
    <div className="p-6">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-2xl font-semibold text-foreground">Auction Listings</h2>
        <Link href="/add-auction">
          <Button data-testid="button-add-auction">
            <Plus className="w-4 h-4 mr-2" />
            Add Auction
          </Button>
        </Link>
      </div>

      {/* Filters */}
      <div className="bg-card rounded-lg border border-border p-4 mb-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          <div>
            <label className="block text-sm font-medium text-muted-foreground mb-2">Search</label>
            <Input
              type="text"
              placeholder="Search auctions..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              data-testid="input-search"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-muted-foreground mb-2">Opal Type</label>
            <Select value={opalType} onValueChange={setOpalType}>
              <SelectTrigger data-testid="select-opal-type">
                <SelectValue placeholder="All Types" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Types</SelectItem>
                <SelectItem value="Black Opal">Black Opal</SelectItem>
                <SelectItem value="Crystal Opal">Crystal Opal</SelectItem>
                <SelectItem value="Boulder Opal">Boulder Opal</SelectItem>
                <SelectItem value="White Opal">White Opal</SelectItem>
                <SelectItem value="Fire Opal">Fire Opal</SelectItem>
              </SelectContent>
            </Select>
          </div>
          <div>
            <label className="block text-sm font-medium text-muted-foreground mb-2">Status</label>
            <Select value={status} onValueChange={setStatus}>
              <SelectTrigger data-testid="select-status">
                <SelectValue placeholder="All Status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Status</SelectItem>
                <SelectItem value="active">Active</SelectItem>
                <SelectItem value="ended">Ended</SelectItem>
                <SelectItem value="won">Won</SelectItem>
                <SelectItem value="lost">Lost</SelectItem>
              </SelectContent>
            </Select>
          </div>
          <div>
            <label className="block text-sm font-medium text-muted-foreground mb-2">Price Range</label>
            <Select value={priceRange} onValueChange={setPriceRange}>
              <SelectTrigger data-testid="select-price-range">
                <SelectValue placeholder="All Prices" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Prices</SelectItem>
                <SelectItem value="$0 - $100">$0 - $100</SelectItem>
                <SelectItem value="$100 - $500">$100 - $500</SelectItem>
                <SelectItem value="$500 - $1000">$500 - $1000</SelectItem>
                <SelectItem value="$1000+">$1000+</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </div>
      </div>

      {/* Auction Table */}
      {isLoading ? (
        <div className="bg-card rounded-lg border border-border p-6">
          <div className="animate-pulse space-y-4">
            {[1, 2, 3, 4, 5].map(i => (
              <div key={i} className="h-16 bg-muted rounded"></div>
            ))}
          </div>
        </div>
      ) : data?.auctions.length === 0 ? (
        <div className="bg-card rounded-lg border border-border p-12 text-center">
          <div className="text-muted-foreground">
            <p className="text-lg mb-2">No auctions found</p>
            <p className="text-sm mb-4">
              {search || opalType || status || priceRange 
                ? "Try adjusting your filters or search terms"
                : "Get started by adding your first auction"
              }
            </p>
            <Link href="/add-auction">
              <Button>Add Your First Auction</Button>
            </Link>
          </div>
        </div>
      ) : (
        <>
          <div className="bg-card rounded-lg border border-border p-4 mb-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-4">
                <div className="text-sm text-muted-foreground">
                  <span className="font-medium text-foreground">{data?.auctions.filter(a => a.status === 'active').length || 0}</span> active auctions
                </div>
                <div className="text-sm text-muted-foreground">
                  <span className="font-medium text-amber-600">{data?.auctions.filter(a => {
                    const end = new Date(a.endTime);
                    const now = new Date();
                    const hoursUntilEnd = (end.getTime() - now.getTime()) / (1000 * 60 * 60);
                    return hoursUntilEnd > 0 && hoursUntilEnd <= 2 && a.status === 'active';
                  }).length || 0}</span> ending soon
                </div>
              </div>
              <div className="text-xs text-muted-foreground">
                üí° Click the $ icon to quickly update bids, or the external link to open Facebook
              </div>
            </div>
          </div>
          <AuctionTable
            auctions={data?.auctions || []}
            formatCurrency={formatCurrency}
            formatDate={formatDate}
            getStatusColor={getStatusColor}
          />
        </>
      )}

      {/* Pagination */}
      {data && data.total > limit && (
        <div className="flex items-center justify-between mt-6 px-6 py-3">
          <div className="text-sm text-muted-foreground">
            Showing {((currentPage - 1) * limit) + 1}-{Math.min(currentPage * limit, data.total)} of {data.total} auctions
          </div>
          <div className="flex space-x-2">
            <Button
              variant="outline"
              size="sm"
              onClick={() => setCurrentPage(Math.max(1, currentPage - 1))}
              disabled={currentPage === 1}
              data-testid="button-previous-page"
            >
              Previous
            </Button>
            {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
              const pageNum = Math.max(1, Math.min(totalPages - 4, currentPage - 2)) + i;
              return (
                <Button
                  key={pageNum}
                  variant={currentPage === pageNum ? "default" : "outline"}
                  size="sm"
                  onClick={() => setCurrentPage(pageNum)}
                  data-testid={`button-page-${pageNum}`}
                >
                  {pageNum}
                </Button>
              );
            })}
            <Button
              variant="outline"
              size="sm"
              onClick={() => setCurrentPage(Math.min(totalPages, currentPage + 1))}
              disabled={currentPage === totalPages}
              data-testid="button-next-page"
            >
              Next
            </Button>
          </div>
        </div>
      )}
    </div>
  );
}

client/src/pages/dashboard.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Link } from "wouter";
import StatsCard from "@/components/stats-card";
import { Gem, Clock, Trophy, DollarSign, Plus, Search, Download, AlertTriangle, RefreshCw } from "lucide-react";
import { Button } from "@/components/ui/button";

interface Analytics {
  totalAuctions: number;
  activeAuctions: number;
  wonAuctions: number;
  avgPrice: number;
  priceRange: { min: number; max: number };
  winRate: number;
  recentAuctions: Array<{
    id: string;
    opalType: string;
    weight: string;
    facebookGroup: string;
    currentBid: string;
    startingBid: string;
    status: string;
    endTime: string;
  }>;
}

export default function Dashboard() {
  const { data: analytics, isLoading } = useQuery<Analytics>({
    queryKey: ["/api/analytics"],
  });

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="animate-pulse space-y-6">
          <div className="h-8 bg-muted rounded w-48"></div>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {[1, 2].map(i => (
              <div key={i} className="bg-card rounded-lg border border-border p-6 h-32"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  const formatCurrency = (value: number) => `$${Math.round(value)}`;
  const getStatusColor = (status: string) => {
    switch (status) {
      case "won": return "bg-accent/10 text-accent";
      case "active": return "bg-amber-500/10 text-amber-600";
      case "lost": return "bg-destructive/10 text-destructive";
      default: return "bg-muted text-muted-foreground";
    }
  };

  return (
    <div className="p-6">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-2xl font-semibold text-foreground">Dashboard</h2>
        <div className="text-sm text-muted-foreground">
          Last updated: <span data-testid="last-updated">just now</span>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
        <StatsCard
          title="Total Auctions"
          value={analytics?.totalAuctions || 0}
          icon={<Gem className="text-primary" />}
          trend={analytics?.totalAuctions ? `${analytics.totalAuctions > 100 ? '+' : ''}${analytics.totalAuctions} tracked` : undefined}
          trendColor="positive"
        />

        <StatsCard
          title="Active Auctions"
          value={analytics?.activeAuctions || 0}
          icon={<Clock className="text-amber-500" />}
          subtitle={`Ending in next 24h: ${Math.floor((analytics?.activeAuctions || 0) / 3)}`}
        />

      </div>

      {/* Recent Activity & Quick Actions */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        <div className="lg:col-span-2 bg-card rounded-lg border border-border">
          <div className="p-6 border-b border-border">
            <h3 className="text-lg font-semibold text-foreground">Recent Auctions</h3>
          </div>
          <div className="p-6">
            {analytics?.recentAuctions?.length === 0 ? (
              <div className="text-center py-8">
                <Gem className="mx-auto h-12 w-12 text-muted-foreground/50" />
                <p className="mt-4 text-muted-foreground">No auctions tracked yet</p>
                <p className="text-sm text-muted-foreground mt-1">
                  <Link href="/add-auction" className="text-primary hover:underline">
                    Add your first auction
                  </Link>
                  {" "}to get started
                </p>
              </div>
            ) : (
              <div className="space-y-4">
                {analytics?.recentAuctions?.map((auction) => (
                  <div key={auction.id} className="flex items-center justify-between p-4 bg-muted/50 rounded-lg">
                    <div className="flex items-center space-x-4">
                      <div className="w-10 h-10 bg-primary/10 rounded-lg flex items-center justify-center">
                        <Gem className="text-primary text-sm" />
                      </div>
                      <div>
                        <p className="font-medium text-foreground">
                          {auction.opalType} - {auction.weight}ct
