client/src/components/charts/price-trend.content:
import { useQuery } from "@tanstack/react-query";
import { TrendingUp } from "lucide-react";

interface Auction {
  id: string;
  currentBid?: string;
  startingBid: string;
  createdAt: string;
  status: string;
}

export default function PriceTrendChart() {
  const { data: auctionsData, isLoading } = useQuery<{ auctions: Auction[] }>({
    queryKey: ["/api/auctions", { limit: 100 }],
  });

  if (isLoading) {
    return (
      <div className="bg-card rounded-lg border border-border p-6">
        <h3 className="text-lg font-semibold text-foreground mb-4">Price Trends (Last 6 Months)</h3>
        <div className="h-64 bg-muted/30 rounded-lg flex items-center justify-center">
          <div className="animate-pulse">Loading chart...</div>
        </div>
      </div>
    );
  }

  const auctions = auctionsData?.auctions || [];

  // Calculate trend data from actual auctions
  const avgPrice = auctions.length > 0 
    ? auctions.reduce((sum, auction) => sum + parseFloat(auction.currentBid || auction.startingBid), 0) / auctions.length
    : 0;

  const wonAuctions = auctions.filter(a => a.status === "won");
  const avgWonPrice = wonAuctions.length > 0
    ? wonAuctions.reduce((sum, auction) => sum + parseFloat(auction.currentBid || auction.startingBid), 0) / wonAuctions.length
    : 0;

  return (
    <div className="bg-card rounded-lg border border-border p-6">
      <h3 className="text-lg font-semibold text-foreground mb-4">Price Trends Analysis</h3>
      <div className="h-64 bg-muted/30 rounded-lg flex flex-col items-center justify-center p-6">
        <TrendingUp className="h-12 w-12 text-primary mb-4" />

        {auctions.length === 0 ? (
          <div className="text-center">
            <p className="text-muted-foreground mb-2">No auction data available</p>
            <p className="text-sm text-muted-foreground">Add auctions to see price trends</p>
          </div>
        ) : (
          <div className="text-center space-y-2">
            <p className="text-sm text-muted-foreground mb-4">Price Trend Summary</p>
            <div className="grid grid-cols-2 gap-4 text-center">
              <div>
                <p className="text-lg font-semibold text-foreground" data-testid="avg-all-price">
                  ${Math.round(avgPrice)}
                </p>
                <p className="text-xs text-muted-foreground">Avg All Auctions</p>
              </div>
              <div>
                <p className="text-lg font-semibold text-foreground" data-testid="avg-won-price">
                  ${Math.round(avgWonPrice)}
                </p>
                <p className="text-xs text-muted-foreground">Avg Won Auctions</p>
              </div>
            </div>
            <p className="text-xs text-muted-foreground mt-4">
              Based on {auctions.length} tracked auctions
            </p>
          </div>
        )}
      </div>
    </div>
  );
}

client/src/components/charts/win-rate-ch.content:
import { PieChart } from "lucide-react";

interface Analytics {
  totalAuctions: number;
  wonAuctions: number;
  winRate: number;
}

interface WinRateChartProps {
  analytics?: Analytics;
}

export default function WinRateChart({ analytics }: WinRateChartProps) {
  const wonCount = analytics?.wonAuctions || 0;
  const totalEnded = analytics?.totalAuctions || 0;
  const lostCount = totalEnded - wonCount;
  const winRate = analytics?.winRate || 0;

  return (
    <div className="bg-card rounded-lg border border-border p-6">
      <h3 className="text-lg font-semibold text-foreground mb-4">Win Rate Analysis</h3>
      <div className="h-64 bg-muted/30 rounded-lg flex flex-col items-center justify-center p-6">
        <PieChart className="h-12 w-12 text-accent mb-4" />

        {totalEnded === 0 ? (
          <div className="text-center">
            <p className="text-muted-foreground mb-2">No completed auctions</p>
            <p className="text-sm text-muted-foreground">Participate in auctions to see win rate analysis</p>
          </div>
        ) : (
          <div className="text-center space-y-4">
            <div>
              <p className="text-3xl font-bold text-foreground" data-testid="win-rate-percentage">
                {Math.round(winRate)}%
              </p>
              <p className="text-sm text-muted-foreground">Win Rate</p>
            </div>

            <div className="flex items-center justify-center space-x-8">
              <div className="text-center">
                <div className="w-4 h-4 bg-accent rounded-full mx-auto mb-2"></div>
                <p className="text-sm font-medium text-foreground" data-testid="won-count">{wonCount}</p>
                <p className="text-xs text-muted-foreground">Won</p>
              </div>
              <div className="text-center">
                <div className="w-4 h-4 bg-destructive rounded-full mx-auto mb-2"></div>
                <p className="text-sm font-medium text-foreground" data-testid="lost-count">{lostCount}</p>
                <p className="text-xs text-muted-foreground">Lost</p>
              </div>
            </div>

            <p className="text-xs text-muted-foreground">
              Based on {totalEnded} completed auctions
            </p>
          </div>
        )}
      </div>
    </div>
  );
}

