// Popup script for Opal Auction Tracker extension
console.log('Popup loaded');

let isMonitoring = false;
let trackerUrl = 'http://localhost:5000';

// Load saved settings
chrome.storage.sync.get(['isMonitoring', 'trackerUrl', 'stats'], (result) => {
