    toggleBtn.textContent = 'Start Monitoring';
    toggleBtn.className = 'button button-primary';
  }
}

// Toggle monitoring
document.getElementById('toggle-monitoring').addEventListener('click', () => {
  isMonitoring = !isMonitoring;

  // Save state
  chrome.storage.sync.set({ isMonitoring });

  // Send message to content script
  chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
    if (tabs[0] && tabs[0].url.includes('facebook.com')) {
      chrome.tabs.sendMessage(tabs[0].id, {
        action: 'toggleMonitoring',
        isMonitoring: isMonitoring
      });
    }
  });

  updateUI();
});

// Open auction tracker
document.getElementById('open-tracker').addEventListener('click', () => {
  chrome.tabs.create({ url: trackerUrl });
});

// Save tracker URL when changed
document.getElementById('tracker-url').addEventListener('change', (e) => {
  trackerUrl = e.target.value;
  chrome.storage.sync.set({ trackerUrl });
});

// Test connection to tracker
async function testConnection() {
  try {
    const response = await fetch(`${trackerUrl}/api/analytics`);
    if (response.ok) {
      console.log('‚úÖ Connected to auction tracker');
    } else {
      console.warn('‚ö†Ô∏è Tracker responded but with error status');
    }
  } catch (error) {
    console.error('‚ùå Cannot connect to auction tracker:', error);
  }
}

// Test connection on popup open
testConnection();

// Listen for messages from content script
chrome.runtime.onMessage.addListener((message, sender, sendResponse) => {
  if (message.action === 'updateStats') {
    document.getElementById('bids-found').textContent = message.stats.bidsFound;
    document.getElementById('auctions-tracked').textContent = message.stats.auctionsTracked;

    // Save stats
    chrome.storage.sync.set({ stats: message.stats });
  }
});

server/auto-monitor.ts.content:
// Automatic URL monitoring system for Facebook auction posts
import { storage } from "./storage";

export class AutoMonitor {
  private interval: NodeJS.Timeout | null = null;
  private isRunning = false;

  async start() {
    if (this.isRunning) return;

    console.log("üîÑ Auto-monitoring temporarily disabled to prevent bid override issues");
    console.log("üí° Use Chrome extension or manual updates for reliable bid tracking");
    this.isRunning = false;

    // Auto-monitoring disabled to prevent Facebook scraper false positives
    // The scraper detects page elements (CSS values, coordinates) as bids
    // Chrome extension provides more accurate real-time detection
  }

  async stop() {
    if (this.interval) {
      clearInterval(this.interval);
      this.interval = null;
    }
    this.isRunning = false;
    console.log("‚èπÔ∏è Stopped automatic monitoring");
  }

  async checkAllAuctions() {
    try {
      // Get all active auctions with Facebook URLs
      const result = await storage.getAuctions({ status: "active" });
      const activeAuctions = result.auctions.filter(auction => 
        auction.postUrl && auction.postUrl.includes('facebook.com')
      );

      if (activeAuctions.length === 0) {
        console.log("üì≠ No active Facebook auctions to monitor");
        return;
      }

      console.log(`üîç Auto-monitoring ${activeAuctions.length} active Facebook auction URLs`);

      // Check each auction URL for bid updates
      for (const auction of activeAuctions) {
        await this.checkSingleAuction(auction);
      }

    } catch (error) {
      console.error("‚ùå Error in auto-monitoring:", error);
    }
  }

  async checkSingleAuction(auction: any) {
    try {
      console.log(`üìä Checking auction ${auction.opalType} (${auction.id}): ${auction.postUrl}`);

      // For now, simulate bid detection since Facebook API is dead
      // In a real implementation, this would:
      // 1. Use puppeteer to scrape the Facebook post
      // 2. Parse comments for bid patterns
      // 3. Extract highest bid and bid count

      const currentBidNum = parseInt(auction.currentBid || auction.startingBid);
      const startingBidNum = parseInt(auction.startingBid);

      // Simulate finding new bids (20% chance of new bid)
      if (Math.random() < 0.2) {
        const newBid = currentBidNum + Math.floor(Math.random() * 50) + 10;
        const bidCount = Math.floor(Math.random() * 10) + 1;

        console.log(`üí∞ NEW BID DETECTED: $${newBid} (was $${currentBidNum}) - ${bidCount} total bids`);

        // Update the auction with new bid info
        await storage.updateAuction(auction.id, {
          currentBid: newBid.toString(),
          bidCount: bidCount,
          lastUpdated: new Date().toISOString()
        });

        console.log(`‚úÖ Updated auction ${auction.id} with new bid: $${newBid}`);
        return true;
      }

      return false;
    } catch (error) {
      console.error(`‚ùå Error checking auction ${auction.id}:`, error);
      return false;
    }
  }

  isActive() {
    return this.isRunning;
  }
}

export const autoMonitor = new AutoMonitor();

server/facebook-scraper.ts.content:
// Advanced Facebook auction bid detector with multiple scraping strategies
import puppeteer from 'puppeteer-extra';
import StealthPlugin from 'puppeteer-extra-plugin-stealth';

// Use stealth plugin to avoid detection
puppeteer.use(StealthPlugin());

export class FacebookScraper {
  private browser: any = null;

  async initialize() {
    try {
      console.log("üîß Initializing advanced Facebook scraper...");
      this.browser = await puppeteer.launch({
        headless: true,
        args: [
          '--no-sandbox',
          '--disable-setuid-sandbox',
          '--disable-web-security',
          '--disable-features=VizDisplayCompositor',
          '--disable-dev-shm-usage',
          '--disable-blink-features=AutomationControlled',
          '--user-agent=Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/121.0.0.0 Safari/537.36'
        ]
      });
      console.log("‚úÖ Advanced Facebook scraper initialized");
    } catch (error) {
      console.error("‚ùå Failed to initialize scraper:", error);
      throw error;
    }
  }

  // Strategy 1: Target ONLY comment-like bid patterns (not all numbers)
  private async extractBidsAdvanced(page: any): Promise<{ bids: number[], comments: string[] }> {
    return await page.evaluate(() => {
      const allBids: number[] = [];
      const allComments: string[] = [];

      // Strategy 1: Look for standalone numbers that are likely bids (not in CSS/technical content)
      // Focus on comment-like structures and user-generated content areas
      const commentAreas = [
        '[role="article"]',
        '[data-testid*="comment"]', 
        'div[dir="auto"]',
        'span[dir="auto"]',
        '.userContent',
        '.comment'
      ];

      commentAreas.forEach(selector => {
        try {
          const elements = document.querySelectorAll(selector);
          for (let i = 0; i < Math.min(elements.length, 50); i++) { // Limit to prevent overflow
            const el = elements[i];
            const text = el.textContent?.trim() || '';

            // Only look for standalone bid numbers (not embedded in long strings)
            if (text.length < 100) { // Short comment-like text only
              const bidMatches = text.match(/^\d{1,3}$/g) || text.match(/\b(\d{1,3})\b/g);
              if (bidMatches && bidMatches.length <= 3) { // Avoid picking up technical data
                bidMatches.forEach(match => {
                  const bid = parseInt(match);
                  if (bid >= 20 && bid <= 200) { // More realistic auction bid range
                    allBids.push(bid);
                    allComments.push(text.substring(0, 50));
                  }
                });
              }
            }
          }
        } catch (e) {
          // Skip problematic selectors
        }
      });

      // Strategy 2: Look for specific comment-like structures
      const commentSelectors = [
        '[data-testid*="comment"]',
        '[role="article"]',
        'div[dir="auto"]',
        'span[dir="auto"]'
      ];

      commentSelectors.forEach(selector => {
        try {
          const elements = document.querySelectorAll(selector);
          for (let i = 0; i < Math.min(elements.length, 100); i++) { // Limit to prevent overflow
            const el = elements[i];
            const text = el.textContent?.trim() || '';

            const bidMatches = text.match(/\b(\d{1,3})\b/g);
            if (bidMatches) {
              bidMatches.forEach(match => {
                const bid = parseInt(match);
                if (bid >= 5 && bid <= 500) {
                  allBids.push(bid);
                  allComments.push(text.substring(0, 50));
                }
              });
            }
          }
        } catch (e) {
          // Skip problematic selectors
        }
      });

      // Strategy 3: Look for time indicators to find recent comments
      const timeIndicators = ['m', 'h', 'min', 'sec', 'now'];
      const allElements = document.querySelectorAll('*');

      for (let i = 0; i < Math.min(allElements.length, 200); i++) { // Limit processing
        const el = allElements[i];
        const text = el.textContent?.toLowerCase() || '';

        if (timeIndicators.some(indicator => text.includes(indicator))) {
          const parent = el.parentElement;
          if (parent) {
            const parentText = parent.textContent || '';
            const bidMatches = parentText.match(/\b(\d{1,3})\b/g);
            if (bidMatches) {
              bidMatches.forEach(match => {
                const bid = parseInt(match);
                if (bid >= 5 && bid <= 500) {
                  allBids.push(bid);
                  allComments.push(parentText.substring(0, 50));
                }
              });
            }
          }
        }
      }

      return { bids: allBids, comments: allComments };
    });
  }

  // Strategy 2: Wait for dynamic content and try multiple times
  private async waitForDynamicContent(page: any, maxWaits: number = 3): Promise<void> {
    for (let i = 0; i < maxWaits; i++) {
      console.log(`üîÑ Loading attempt ${i + 1}/${maxWaits}...`);

      // Wait for content
      await new Promise(resolve => setTimeout(resolve, 2000));

      // Try to trigger more comment loading
      try {
        await page.evaluate(() => {
          // Scroll to trigger lazy loading
          window.scrollTo(0, document.body.scrollHeight);

          // Click potential "View more" buttons
          const buttons = document.querySelectorAll('div[role="button"], span[role="button"]');
          buttons.forEach((btn: any) => {
            const text = btn.textContent?.toLowerCase() || '';
            if (text.includes('view') || text.includes('more') || text.includes('comment') || 
                text.includes('show') || text.includes('load')) {
              try {
                btn.click();
              } catch (e) {
                // Ignore click errors
              }
            }
          });
        });

        await new Promise(resolve => setTimeout(resolve, 1000));
      } catch (e) {
        console.log(`‚ö†Ô∏è Loading attempt ${i + 1} had issues, continuing...`);
      }
    }
  }

  async scrapeFacebookPost(url: string): Promise<{ currentBid: number | null, bidCount: number, comments: string[] }> {
    if (!this.browser) {
      await this.initialize();
    }

    // Clean URL to prevent duplication
    const cleanUrl = url.replace(/\/https:\/\/.*$/, '');

    console.log(`üéØ Advanced scraping: ${cleanUrl}`);

    const page = await this.browser.newPage();

    try {
      // Set realistic browser properties
      await page.setUserAgent('Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/121.0.0.0 Safari/537.36');
      await page.setViewport({ width: 1366, height: 768 });

      // Add extra headers to look more authentic
      await page.setExtraHTTPHeaders({
        'Accept-Language': 'en-US,en;q=0.9',
        'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,*/*;q=0.8'
      });

      console.log(`üì° Navigating to Facebook post...`);
      await page.goto(cleanUrl, { 
        waitUntil: 'networkidle0',
        timeout: 45000 
      });

      // Wait for and load dynamic content multiple times
      await this.waitForDynamicContent(page, 4);

      console.log(`üîç Extracting bids with advanced detection...`);
      const result = await this.extractBidsAdvanced(page);

      console.log(`üìä Found ${result.bids.length} potential bid numbers: [${result.bids.join(', ')}]`);

      // No fallback to avoid picking up CSS/technical numbers
      if (result.bids.length === 0) {
        console.log(`üîç No auction bids detected in comment areas`);
      }

      // Calculate results safely
      const allBids = result.bids.filter(bid => bid > 0 && bid <= 500);
      const uniqueBids = [...new Set(allBids)].sort((a, b) => b - a); // Remove duplicates and sort descending
      const currentBid = uniqueBids.length > 0 ? uniqueBids[0] : null;

      console.log(`üèÜ HIGHEST BID DETECTED: $${currentBid || 'None'}`);
      console.log(`üë• UNIQUE BIDS FOUND: [${uniqueBids.slice(0, 10).join(', ')}]`);
      console.log(`üìà TOTAL BIDS: ${allBids.length}`);

      return {
        currentBid,
        bidCount: allBids.length,
        comments: result.comments.slice(0, 5) // Limit comments to prevent overflow
      };

    } catch (error) {
      console.error(`‚ùå Advanced scraping failed:`, error);
      return { currentBid: null, bidCount: 0, comments: [] };
    } finally {
      await page.close();
    }
  }

  async close() {
    if (this.browser) {
      await this.browser.close();
      this.browser = null;
    }
  }
}

export const facebookScraper = new FacebookScraper();
